    @Override
    public void onClick(View view) {
        switch (view.getId()) {

            case R.id.btnNewRequest:


                Fragment fragment = FragmentCreateObs.newInstance("", "");
                FragmentTransaction transaction = getActivity().getSupportFragmentManager().beginTransaction();
                transaction.replace(R.id.container, fragment);
                transaction.addToBackStack(null);
                transaction.commit();
                break;
        }
    }


    private void showDialog(final View view, CharSequence[] charSequenceArr, String title, Observer event) {
        AlertDialog.Builder builder = new AlertDialog.Builder(getActivity());
        builder.setTitle(title);
        builder.setSingleChoiceItems(charSequenceArr, -1, new DialogInterface.OnClickListener() {
            public void onClick(DialogInterface dialogInterface, int i) {
                switch (i) {
                    case 0:

                        Toast.makeText(getActivity(), "حذف", Toast.LENGTH_SHORT).show();
                        eventListPresenter.deleteObs(event.getO_id(),user_id);

                        break;
                    case 1:
                        eventListPresenter.editObs(event);
                        Toast.makeText(getActivity(), "ویرایش", Toast.LENGTH_SHORT).show();
                        break;
                    case 2:
                        eventListPresenter.editObs(event);
                        Toast.makeText(getActivity(), "اطلاعات", Toast.LENGTH_SHORT).show();
                        break;
                }

                dialogInterface.dismiss();
            }
        });
        builder.show();
    }
